mapper.select:
SELECT 

FROM sys_config a

WHERE a.id = #{id}

SELECT 

FROM sys_config a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM sys_config a


